#include <stdio.h>


int main(){ 
    float x = 5.5;
    float y = 2.2;
    float soma = x + y;
    float diferenca = x - y;
    float produto = x * y;
    float quoeficiente = x / y; //Divisão de ponto flutante

    printf("Soma: %.2f\n", soma);
    printf("Difereça: %.2f\n", diferenca);
    printf("Produto: %.2f\n", produto);
    printf("Quoeficiente: %.2f\n", quoeficiente);




    return 0;


}    